import React, { useState } from 'react';
import axios from 'axios';
import backgroundImage from './bg1.jpg'; // Replace 'background-image.jpg' with the path to your image file

function Feedback() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [feedback, setFeedback] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:3001/feedback', { name, email, feedback });
            setMessage('Feedback submitted successfully');
            setName('');
            setEmail('');
            setFeedback('');
        } catch (error) {
            console.error('Error submitting feedback:', error);
            setMessage('Failed to submit feedback');
        }
    };

    return (
        <div style={{ display: 'flex', backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center', minHeight: '100vh' }}>
            <div className="container" style={{ flex: 1 }}>
                <div>
                    <h1 className="text-danger">About Us</h1>
                    <p className="text-dark">Our Feedback System is designed to gather valuable feedback from our users to continuously improve our services. It allows users to provide their name, email, and feedback in a simple and efficient manner.</p>
                    <p className="text-dark">The system is built using modern web technologies, including HTML, CSS, JavaScript, and Bootstrap. It utilizes React.js for dynamic user interfaces and state management.</p>
                    <p className="text-dark">We value the opinions and suggestions of our users and strive to create a user-friendly platform that meets their needs.</p>
                    <h2 className="text-danger">System Details</h2>
                    <ul className="text-dark">
                        <li>Frontend Framework: React.js</li>
                        <li>Styling Framework: Bootstrap</li>
                        <li>Backend Technology: [Insert Backend Technology Here]</li>
                        <li>Database: [Insert Database Technology Here]</li>
                        <li>Hosting: [Insert Hosting Platform Here]</li>
                    </ul>
                </div>
            </div>
            <div className="container" style={{ flex: 1 }}>
                <h1 className="text-danger">Feedback Form</h1>
                <form onSubmit={handleSubmit} className="text-dark">
                    <div className="form-group">
                        <label htmlFor="name">Name:</label>
                        <input type="text" className="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email:</label>
                        <input type="email" className="form-control" id="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="feedback">Feedback:</label>
                        <textarea className="form-control" id="feedback" value={feedback} onChange={(e) => setFeedback(e.target.value)}></textarea>
                    </div>
                    <button type="submit" className="btn btn-danger">Submit Feedback</button>
                </form>
                {message && <p className="text-danger">{message}</p>}
            </div>
        </div>
    );
}

export default Feedback;
