import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link for navigation
import backgroundImage from './bg1.jpg';

function Recommendations() {
  const [userInput, setUserInput] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/recommend', {
        user_input: userInput
      });
      setRecommendations(response.data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
      setError('Failed to fetch recommendations');
    }
  };

  return (
    <div style={{ display: 'flex', backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center', minHeight: '100vh' }}>
  <div style={{ backgroundColor: 'rgba(255, 255, 255, 0.7)', padding: '20px', borderRadius: '10px' }}>
    <div className="row mt-4">
      <div className="col">
        <h1>Book Recommendation System</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="user_input">Enter a book title:</label>
            <input
              type="text"
              id="user_input"
              name="user_input"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              className="form-control"
            />
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>

        <h2 className="mt-4">Recommendations:</h2>
        <div className="row">
          {recommendations.map((item, index) => (
            <div key={index} className="col-md-3 mb-4">
              <div className="card" style={{ backgroundColor: 'white' }}>
                <img src={item.image_url} className="card-img-top" alt="Book cover" />
                <div className="card-body">
                  <h5 className="card-title">{item.title}</h5>
                  <p className="card-text">Author: {item.author}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  </div>

  {/* Navigation Panel */}
  <div style={{ textAlign: 'right', backgroundColor: 'red', padding: '10px', borderRadius: '5px', marginBottom: '20px' }}>
    <Link to="/feedback" style={{ color: 'white', textDecoration: 'none', transition: 'color 0.3s', border: '1px solid transparent', borderRadius: '3px', padding: '5px' }}>
      About Us
    </Link>
  </div>
</div>

  );
}

export default Recommendations;
