import React, { useState } from 'react';

const Signup = () => {
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Here you can add your login logic, like sending a request to your server
        // For demonstration, I'm just checking if the username and password are both 'admin'
        if (username === 'admin' && password === 'admin') {
            // Successful login
            console.log('Login successful');
        } else {
            // Failed login
            setError('Invalid username or password');
        }
    };

    return (
        
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 rounded-0">
                    <div className="card rounded-0 bg-dark text-white">
                        <div className="card-header text-center">Signup</div>
                        <div className="card-body">
                            {error && <div className="alert alert-danger">{error}</div>}
                            <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                    <label htmlFor="email">EMail</label>
                                    <input
                                        type="text"
                                        className="form-control rounded-0"
                                        id="email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="username">Username</label>
                                    <input
                                        type="text"
                                        className="form-control rounded-0"
                                        id="username"
                                        value={username}
                                        onChange={(e) => setUsername(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="password">Password</label>
                                    <input
                                        type="password"
                                        className="form-control rounded-0"
                                        id="password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className='mt-2 '>
                                    <button  className="btn btn-success d-block mb-2 text-center rounded-0 w-100">Signup</button>
                                    <button className="btn btn-primary d-block text-center rounded-0 w-100">Create Account</button>
                                </div>
                             </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        
    );
};

export default Signup;
