import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import validation from './Signupval';
import axios from 'axios';


function Signup  ()  {
    const navigate = useNavigate();
    const [error, setError] = useState({});
    const [values,setValues] = useState({
        username:'',
        email: '',
        password: ''
    })
    

    const handleInput =(event) => {
        setValues(prev => ({ ...prev, [event.target.name]: event.target.value }));
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setError(validation(values, () => {
            if (error.username === "" && error.email === "" && error.password === ""){
            axios.post('http://localhost:3001/signup', values)
            .then(res => {
                navigate('/');
            })
            .catch(res => console.log(res));
        }
    }));

    return (
        
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 rounded-0">
                    <div className="card rounded-0 bg-dark.bg-gradient text-black">
                        <div className="card-header text-center">Signup</div>
                        <div className="card-body">
                            <form >
                            <div className="form-group">
                                    <label htmlFor="email">E Mail</label>
                                    <input
                                        type="email"
                                        className="form-control rounded-0"
                                        id="email"
                                        name='email'
                                        // value={email}
                                        // onChange={(e) => setEmail(e.target.value)}
                                        onChange={handleInput}
                                        />
                                         {error.email && <span className='text-danger'>{error.email}</span>}
                                </div>
                                <div className="form-group">
                                    <label htmlFor="username">Username</label>
                                    <input
                                        type="text"
                                        className="form-control rounded-0"
                                        id="username"
                                        name='username'
                                        // value={username}
                                        // onChange={(e) => setUsername(e.target.value)}
                                        onChange={handleInput}
                                    />
                                     {error.username && <span className='text-danger'>{error.username}</span>}
                                </div>
                                <div className="form-group">
                                    <label htmlFor="password">Password</label>
                                    <input
                                        type="password"
                                        className="form-control rounded-0"
                                        id="password"
                                        name='password'
                                        // value={password}
                                        // onChange={(e) => setPassword(e.target.value)}
                                        onChange={handleInput}
                                    />
                                    {error.password && <span className='text-danger'>{error.password}</span>}
                                </div>
                                
                                    <button  type="submit"  onClick={handleSubmit} className="btn btn-success d-block mb-2 text-center rounded-0 w-100">Signup</button>
                                    <Link to="/Home" className="btn btn-light d-block text-center rounded-0 w-100 text-decoration-none">Login</Link>
                                
                             </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        
    );
};
}
export default Signup;
