import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Home() {
  const [userInput, setUserInput] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [topBooks, setTopBooks] = useState([]);

  useEffect(() => {
    const fetchTopBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/');
        setTopBooks(response.data);
      } catch (error) {
        console.error('Error fetching top books:', error);
      }
    };

    fetchTopBooks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/recommend', {
        user_input: userInput
      });
      setRecommendations(response.data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <h1>Top 50 Books</h1>
          <ul className="list-group">
            {topBooks.map((top_books, index) => (
              <li key={index} className="list-group-item">
                <div>Title: {top_books.book_name}</div>
                <div>Author: {top_books.author}</div>
                <img src={top_books.image} alt="Book cover" className="img-fluid" />
                <div>Votes: {top_books.votes}</div>
                <div>Rating: {top_books.rating}</div>
              </li>
            ))}
          </ul>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col">
          <h1>Book Recommendation System</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="user_input">Enter a book title:</label>
              <input
                type="text"
                id="user_input"
                name="user_input"
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                className="form-control"
              />
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>

          <h2 className="mt-4">Recommendations:</h2>
          <ul className="list-group">
            {recommendations.map((item, index) => (
              <li key={index} className="list-group-item">
                <div>Title: {item.title}</div>
                <div>Author: {item.author}</div>
                <img src={item.image_url} alt="Book cover" className="img-fluid" />
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Home;
