import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Home() {
  const [userInput, setUserInput] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [topBooks, setTopBooks] = useState([]);

  useEffect(() => {
    const fetchTopBooks = async () => {
      try {
        const response = await axios.post('http://localhost:5000/');
        setTopBooks(response.data);
      } catch (error) {
        console.error('Error fetching top books:', error);
      }
    };

    fetchTopBooks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/recommend', {
        user_input: userInput
      });
      setRecommendations(response.data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  return (
    <div>
      <div>
        <h1>Top 50 Books</h1>
        <ul>
          {topBooks.map((top_books, index) => (
            <li key={index}>
              <div>Title: {top_books.book_name}</div>
              <div>Author: {top_books.author}</div>
              <img src={top_books.image} alt="Book cover" />
              <div>Votes: {top_books.votes}</div>
              <div>Rating: {top_books.rating}</div>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h1>Book Recommendation System</h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="user_input">Enter a book title:</label><br />
          <input
            type="text"
            id="user_input"
            name="user_input"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
          /><br />
          <button type="submit">Submit</button>
        </form>

        <h2>Recommendations:</h2>
        <ul>
          {recommendations.map((item, index) => (
            <li key={index}>
              <div>Title: {item.title}</div>
              <div>Author: {item.author}</div>
              <img src={item.image_url} alt="Book cover" />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Home;
