import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Home() {
  const [topBooks, setTopBooks] = useState([]);
  const [error, setError] = useState(null); // Add state for error

  useEffect(() => {
    const fetchTopBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/');
        setTopBooks(response.data);
      } catch (error) {
        console.error('Error fetching top books:', error);
        setError('Failed to fetch top books');
      }
    };

    fetchTopBooks();
  }, []);

  return (
    <div className="container">
      {error && <div className="alert alert-danger">{error}</div>} {/* Display error if present */}
      <div className="row">
        <div className="col">
          <h1>Top 50 Books</h1>
          <Link to="/recommend">
            <button className="btn btn-primary mb-3">Go to Recommendations</button>
          </Link>
          <div className="row">
            {topBooks.map((topBook, index) => (
              <div key={index} className="col-md-4 mb-4">
                <div className="card">
                  <img src={topBook.image} className="card-img-top" alt="Book cover" />
                  <div className="card-body">
                    <h5 className="card-title">{topBook.book_name}</h5>
                    <p className="card-text">Author: {topBook.author}</p>
                    <p className="card-text">Votes: {topBook.votes}</p>
                    <p className="card-text">Rating: {topBook.rating}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
