import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import validation from './Signupval';


const Signup = () => {
    const [error, setError] = useState({});
    const [values,setValues] = useState({
        username:'',
        email: '',
        password: ''
    })

    const handleInput =(event) => {
        setValues(prev => ({...prev, [event.target.name]:[event.target.value]}))
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setError(validation(values));
    };


    return (
        
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 rounded-0">
                    <div className="card rounded-0 bg-dark.bg-gradient text-black">
                        <div className="card-header text-center">Signup</div>
                        <div className="card-body">
                            {error && <div className="alert alert-danger">{error}</div>}
                            <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                    <label htmlFor="email">EMail</label>
                                    <input
                                        type="text"
                                        className="form-control rounded-0"
                                        id="email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="username">Username</label>
                                    <input
                                        type="text"
                                        className="form-control rounded-0"
                                        id="username"
                                        value={username}
                                        onChange={(e) => setUsername(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="password">Password</label>
                                    <input
                                        type="password"
                                        className="form-control rounded-0"
                                        id="password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className='mt-2 '>
                                    <button  className="btn btn-success d-block mb-2 text-center rounded-0 w-100">Signup</button>
                                    <Link to="/Home" className="btn btn-light d-block text-center rounded-0 w-100 text-decoration-none">Login</Link>
                                </div>
                             </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        
    );
};

export default Signup;
