import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate instead of useHistory
import validation from './Signupval';
import axios from 'axios';

function Signup() {
    const navigate = useNavigate(); // Use useNavigate hook instead of useHistory
    const [error, setError] = useState({});
    const [values, setValues] = useState({
        username: '',
        email: '',
        password: ''
    });

    const handleInput = (event) => {
        setValues(prev => ({ ...prev, [event.target.name]: event.target.value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setError(validation(values, () => {
            if (error.username === "" && error.email === "" && error.password === "") {
                axios.post('http://localhost:3001/signup', values)
                    .then(res => {
                        // Redirect to another route upon successful signup
                        navigate('/'); // Redirect to the home page
                    })
                    .catch(res => console.log(res));
            }
        }));
    };


    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 rounded-0">
                    <div className="card rounded-0 bg-dark.bg-gradient text-black">
                        <div className="card-header text-center">Signup</div>
                        <div className="card-body">
                            <form>
                                {/* Form fields */}
                                <button type="submit" onClick={handleSubmit} className="btn btn-success d-block mb-2 text-center rounded-0 w-100">Signup</button>
                                <Link to="/Home" className="btn btn-light d-block text-center rounded-0 w-100 text-decoration-none">Login</Link>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Signup;
