import React, { useState } from 'react';
import axios from 'axios';

function Feedback() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [feedback, setFeedback] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:3001/feedback', { name, email, feedback });
            setMessage('Feedback submitted successfully');
            setName('');
            setEmail('');
            setFeedback('');
        } catch (error) {
            console.error('Error submitting feedback:', error);
            setMessage('Failed to submit feedback');
        }
    };

    return (
        <div class="container">
    <h1 class="text-danger">Feedback Form</h1>
    <form onSubmit={handleSubmit} class="text-dark">
        <div class="form-group">
            <label for="name">Name:</label>
            <input type="text" class="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div class="form-group">
            <label for="email">Email:</label>
            <input type="email" class="form-control" id="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div class="form-group">
            <label for="feedback">Feedback:</label>
            <textarea class="form-control" id="feedback" value={feedback} onChange={(e) => setFeedback(e.target.value)}></textarea>
        </div>
        <button type="submit" class="btn btn-danger">Submit Feedback</button>
    </form>
    {message && <p class="text-danger">{message}</p>}
</div>
    );

}

export default Feedback;
