import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Home() {
  const [userInput, setUserInput] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [topBooks, setTopBooks] = useState([]);

  useEffect(() => {
    const fetchTopBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/');
        setTopBooks(response.data);
      } catch (error) {
        console.error('Error fetching top books:', error);
      }
    };

    fetchTopBooks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/recommend', {
        user_input: userInput
      });
      setRecommendations(response.data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <h1>Top 50 Books</h1>
          <div className="row">
            {topBooks.map((top_book, index) => (
              <div key={index} className="col-md-4 mb-4">
                <div className="card">
                  <img src={top_book.image} className="card-img-top" alt="Book cover" />
                  <div className="card-body">
                    <h5 className="card-title">{top_book.book_name}</h5>
                    <p className="card-text">Author: {top_book.author}</p>
                    <p className="card-text">Votes: {top_book.votes}</p>
                    <p className="card-text">Rating: {top_book.rating}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col">
          <h1>Book Recommendation System</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="user_input">Enter a book title:</label>
              <input
                type="text"
                id="user_input"
                name="user_input"
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                className="form-control"
              />
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>

          <h2 className="mt-4">Recommendations:</h2>
          <div className="row">
            {recommendations.map((item, index) => (
              <div key={index} className="col-md-4 mb-4">
                <div className="card">
                  <img src={item.image_url} className="card-img-top" alt="Book cover" />
                  <div className="card-body">
                    <h5 className="card-title">{item.title}</h5>
                    <p className="card-text">Author: {item.author}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
